    public String getNumber(){
        return jTextField1.getText().trim();
    }
    public void setTextArea(String beati, boolean check){//dep hay ko, xem nguoi dung có check hay ko
        jTextArea1.setText(beati+"\n");
        String name = jTextField2.getText();//lay ra
        String[] ss = name.split("\\s+");//tach nhau boi dau cách
        String email = ss[ss.length-1];//lay duoc ten Uyen
        for (int i = 0; i < ss.length-1; i++) {//khong lay chu cuoi cua may cai ho dem
            email += Character.toUpperCase(ss[i].charAt(0));
            
        }
        email += "@ptit.edu.vn";
        if(check){
            jTextArea1.append("Email: "+email);
        }
    }
