    public String getNumber(){
        return jTextField1.getText().trim();
    }
    public void setTextArea(String beati, boolean check){
        jTextArea1.setText(beati +"\n");//kiem tra xem co phai so dem ko
        String name = jTextField2.getText().trim();
        String[] ss = name.split("\\s+");
        String email = ss[ss.length-1];//lay duoc ten Uyen
        for (int i = 0; i < ss.length-1; i++) {
           email+= Character.toUpperCase(ss[i].charAt(0));
            
        }
        email += "@ptit.edu.vn";
        if(check){
            jTextArea1.append("Email: " +email);
        }
    }
    
